  `id` int unsigned NOT NULL AUTO_INCREMENT COMMENT '序号',
  `username` varchar(50) NOT NULL COMMENT '用户名',
  `invite_code` varchar(50) NOT NULL DEFAULT '' COMMENT '邀请码',
  `password` varchar(32) NOT NULL DEFAULT '0' COMMENT '登录密码',
  `money` decimal(20,4) NOT NULL DEFAULT '0.0000' COMMENT '余额',
  `frozen_money` decimal(20,4) NOT NULL DEFAULT '0.0000' COMMENT '冻结金额',
  `savings_flexible` decimal(20,4) NOT NULL DEFAULT '0.0000' COMMENT '储蓄金活期',
  `savings_fixed` decimal(20,4) NOT NULL DEFAULT '0.0000' COMMENT '储蓄金定期',
  `value` int NOT NULL DEFAULT '0' COMMENT '积分',
  `mid` int NOT NULL DEFAULT '0' COMMENT '会员等级id',
  `income` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '总收益金额',
  `integral` int NOT NULL DEFAULT '0' COMMENT '积分',
  `point` int NOT NULL DEFAULT '0' COMMENT '签到积分',
  `draw_num` int NOT NULL DEFAULT '0' COMMENT '抽奖次数',
  `auth_email` tinyint(1) NOT NULL DEFAULT '0' COMMENT '邮箱认证,0未认证/1已认证',
  `email` varchar(50) DEFAULT NULL COMMENT '邮箱',
  `auth_phone` tinyint(1) NOT NULL DEFAULT '0' COMMENT '手机号认证,0未认证/1已认证',
  `phone` varchar(50) DEFAULT NULL COMMENT '手机号',
  `auth_google` tinyint(1) NOT NULL DEFAULT '0' COMMENT '谷歌认证,0未认证/1已认证',
  `google_key` varchar(50) DEFAULT NULL COMMENT '谷歌验证密钥',
  `google_qrcode` varchar(500) DEFAULT NULL COMMENT '谷歌验证二维码',
  `usdt_address` varchar(255) DEFAULT NULL COMMENT 'USDT地址',
  `usdt_qrcode` text COMMENT 'USDT二维码',
  `ip` varchar(20) DEFAULT '0.0.0.0' COMMENT 'IP',
  `agent` int NOT NULL DEFAULT '10000' COMMENT '所属代理线',
  `clock` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否锁定,0否/1是',
  `logintime` varchar(50) NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '登录时间',
  `time` varchar(50) NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '注册时间',
  `time_zone` varchar(50) NOT NULL COMMENT '时区',
  `act_time` varchar(50) NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '实际时间',
  `lastip` varchar(20) DEFAULT '0.0.0.0' COMMENT '最後登錄IP',
  `device` tinyint(1) NOT NULL DEFAULT '0' COMMENT '登錄環境\r\n0、網頁\r\n1、app',
  `system_user_id` int DEFAULT '0' COMMENT '系统管理员id',
  `withdrawable` decimal(10,2) DEFAULT '0.00' COMMENT '可提现余额',
  `is_invite` tinyint DEFAULT '1' COMMENT '是否可邀请：1是，0否',
  `is_recharge` tinyint DEFAULT '1' COMMENT '是否可以充值：1是；0:否',
  `is_withdrawal` tinyint DEFAULT '1' COMMENT '是否可以提现：1是；0:否',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE KEY `username` (`username`),
  UNIQUE KEY `invite_code` (`invite_code`)
) ENGINE=MyISAM AUTO_INCREMENT=34726 DEFAULT CHARSET=utf8mb3 ROW_FORMAT=DYNAMIC;

